package troll.kotlin.dialogimport android.view.LayoutInflaterimport com.blankj.utilcode.util.ScreenUtilsimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.eth.base.viewbinding.dialog.DialogLocalimport troll.kotlin.databinding.ActivityDialogBinding/** * author : TangPeng * date : 5/19/22 16:48 * description : */class DialogActivity : BaseActivity<ActivityDialogBinding>() {    override fun getBinding(inflater: LayoutInflater): ActivityDialogBinding =        ActivityDialogBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {        val dialogFrag = DialogFrag()        dialogFrag.local = DialogLocal.CENTER        dialogFrag.isWidthMatchParent = true        dialogFrag.setDialogHeight(ScreenUtils.getScreenHeight())        bd.dialogShow.onClick {            dialogFrag.showDialog(supportFragmentManager, "测试")        }        bd.dialogDismiss.onClick {            dialogFrag.dismiss()        }    }}