package troll.kotlin.dialogimport android.view.LayoutInflaterimport android.view.WindowManagerimport com.gyf.immersionbar.ImmersionBarimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.eth.base.viewbinding.dialog.DialogLocalimport troll.kotlin.Rimport troll.kotlin.databinding.ActivityDialogBinding/** * author : TangPeng * date : 5/19/22 16:48 * description : */class DialogActivity : BaseActivity<ActivityDialogBinding>() {    override fun getBinding(inflater: LayoutInflater): ActivityDialogBinding =        ActivityDialogBinding.inflate(inflater)    override fun flowData() {        //设置共同沉浸式样式        ImmersionBar.with(this).navigationBarColor(R.color.color_80000000).init()    }    override fun flowView() {        val dialogFrag = DialogFrag()        dialogFrag.full = true        dialogFrag.local = DialogLocal.CENTER        dialogFrag.isWidthMatchParent = true        dialogFrag.setDialogHeight(WindowManager.LayoutParams.MATCH_PARENT)        bd.dialogShow.onClick {            dialogFrag.showDialog(supportFragmentManager, "测试")        }        bd.dialogDismiss.onClick {            dialogFrag.dismiss()        }    }}