package troll.kotlin.flowimport android.view.LayoutInflaterimport com.troll.bus.Busimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityFlowBindingclass FlowActivity : BaseActivity<ActivityFlowBinding>() {    override fun getBinding(inflater: LayoutInflater): ActivityFlowBinding =        ActivityFlowBinding.inflate(inflater)    override fun flowData() {        Bus.get().with(this).onValue<String>(1) {            println("数据接收$it")        }    }    override fun flowView() {        bd.busButtonSend.onClick {            Bus.get().send(1, "测试发送")        }    }}