package troll.kotlin.flowimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport androidx.lifecycle.lifecycleScopeimport kotlinx.coroutines.launchimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityFlowBindingclass FlowActivity : BaseActivity<ActivityFlowBinding>() {    private val vm: FlowViewModel by viewModels()    // select 操作符    private val selectVm: SelectViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityFlowBinding =        ActivityFlowBinding.inflate(inflater)    override fun flowData() {        lifecycleScope.launch {            vm.timeFlow().collect { time ->                bd.busTv.text = time            }        }    }    override fun flowView() {    }}