package troll.kotlin.flowimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport androidx.lifecycle.lifecycleScopeimport kotlinx.coroutines.launchimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityFlowBindingclass FlowActivity : BaseActivity<ActivityFlowBinding>() {    private val vm: FlowViewModel by viewModels()    // select 操作符    private val selectVm: SelectViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityFlowBinding =        ActivityFlowBinding.inflate(inflater)    override fun flowData() {        lifecycleScope.launch {            vm.timeFlow().collect { time ->                bd.busTv.text = time            }        }        lifecycleScope.launch {            vm.stateFlow.collect {                bd.busTv.text = it            }        }        lifecycleScope.launch {            vm.sharedFlow.collect {                bd.busTv.text = it            }        }    }    override fun flowView() {        bd.kotlinFlowFlowSharedflow.onClick {            vm.sharedFlow()        }    }}