package troll.kotlin.fragmentimport android.os.Bundleimport android.os.PersistableBundleimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.Rimport troll.kotlin.databinding.ActivityFragmentBinding/** * author : TangPeng * date : 6/19/22 16:57 * description : */class FragmentActivity : BaseActivity<ActivityFragmentBinding>() {    private val fragment by lazy {        TestFragment()    }    private val appleFragment by lazy {        AppleFragment()    }    private val vm: FragViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityFragmentBinding =        ActivityFragmentBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {        bd.fragmentButtonAdd.onClick {            supportFragmentManager.beginTransaction()                .replace(R.id.fragment_content, fragment).commit()        }        bd.fragmentButtonHide.onClick {            supportFragmentManager.beginTransaction()                .replace(R.id.fragment_content, appleFragment).commit()        }        bd.fragmentButtonChange.onClick {            FragViewModel.type = "222222"        }    }    override fun onBackPressed() {        super.onBackPressed()        FragViewModel.type = "1111111"    }    override fun onRestoreInstanceState(savedInstanceState: Bundle) {        super.onRestoreInstanceState(savedInstanceState)        println("TestFragment::savedInstanceState:::$savedInstanceState")    }    override fun onSaveInstanceState(outState: Bundle, outPersistentState: PersistableBundle) {        super.onSaveInstanceState(outState, outPersistentState)        println("TestFragment::onSaveInstanceState::outState$outState::::outPersistentState:::$outPersistentState")    }}