package troll.kotlin.functionimport android.view.View/** * author : TangPeng * date : 3/17/22 20:37 * description : 函数的理解 * * 高阶函数：将函数用作参数或者返回值的函数 */object Function {    // 可以利用inline的函数    //   (View) ->Unit : 函数类型  参数    var click: ((View) -> Unit?)? = null    fun setOnClick(l: (View) -> Unit) {        this.click = l    }    // 函数类型    // ::add  函数引用    val t: (Int, Int) -> Int = ::add    fun add(a: Int, b: Int): Int {        "".test {        }        "".apply {        }        "".also {        }        return a + b    }    /**     * SAM转换     * 一个抽象方法的类或者接口     */    /**     * 带接收者的函数类型     *     * (T) -> Unit     * T.() -> Unit     *     */}fun String.test(block: String.() -> Unit) {    block()}