package troll.kotlin.function/** * author : TangPeng * date : 10/20/22 07:33 * description : */class FunctionCollection {    fun test() {        val stringLength: String = lambdaFunction("ceshi")        println(stringLength)        stringAdd("匿名函数参数", lambdaFunction)        stringAdd("具名函数调用", ::stringDelete)    }    fun stringStrip(): (String) -> String {        return {            "函数作为返回值"        }    }    val lambdaFunction: (String) -> String = {        val content = if (it.length > 10) {            it.plus("长度不够10")        } else {            it.plus("长度大于10")        }        content    }    fun stringDelete(string: String) = if (string.length > 10) {        "具名函数调用"    } else {        string    }    fun stringAdd(s: String, add: (String) -> String) {        add(s)    }}