package troll.kotlin.jetpackimport androidx.lifecycle.DefaultLifecycleObserverimport androidx.lifecycle.LifecycleOwnerimport troll.btc.log.TLog/** * author : TangPeng * date : 3/22/23 10:36 * description : */class DefaultLifeOb : DefaultLifecycleObserver {    private val TAG = "DefaultLifeOb"    override fun onCreate(owner: LifecycleOwner) {        super.onCreate(owner)        TLog.i(TAG, "onCreate")    }    override fun onResume(owner: LifecycleOwner) {        super.onResume(owner)        TLog.i(TAG, "onResume")    }    override fun onPause(owner: LifecycleOwner) {        super.onPause(owner)        TLog.i(TAG, "onPause")    }    override fun onStart(owner: LifecycleOwner) {        super.onStart(owner)        TLog.i(TAG, "onStart")    }    override fun onStop(owner: LifecycleOwner) {        super.onStop(owner)        TLog.i(TAG, "onStop")    }}