package troll.kotlin.jetpackimport androidx.lifecycle.Lifecycleimport androidx.lifecycle.LifecycleObserverimport androidx.lifecycle.OnLifecycleEventimport troll.btc.log.TLog/** * author : TangPeng * date : 3/21/23 16:56 * description : */class LifecycleOb : LifecycleObserver {    private val TAG = "LifecycleOb"    @OnLifecycleEvent(Lifecycle.Event.ON_RESUME)    fun connectListener() {        TLog.i(TAG, "connectListener")    }    @OnLifecycleEvent(Lifecycle.Event.ON_PAUSE)    fun disconnectListener() {        TLog.i(TAG, "disconnectListener")    }}