package troll.kotlin.jetpackimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityLiveDataBinding/** * author : TangPeng * date : 4/12/22 15:12 * description : */class LiveDataActivity : BaseActivity<ActivityLiveDataBinding>() {    private val vm: LiveDataViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityLiveDataBinding =        ActivityLiveDataBinding.inflate(inflater)    override fun flowData() {        vm.getOne().observe(this) {            bd.liveSubmit.text = it        }    }    override fun flowView() {        bd.liveSubmit.onClick {            vm.transferData()        }    }}