package troll.kotlin.notificationimport android.app.NotificationChannelimport android.app.NotificationManagerimport android.content.Contextimport android.os.Build/** * author : TangPeng * date : 8/9/22 15:44 * description : */object NotificationManager {    private const val STEP_CHANNEL_CORE = "Channel_Id_Core"    private const val STEP_CHANNEL_RECOMMEND = "Channel_Id_Recommend"    private const val STEP_CHANNEL_TOOLS = "Channel_Id_Tools"    fun createChannel(context: Context) {        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {            val notificationManager =                context.getSystemService(Context.NOTIFICATION_SERVICE) as NotificationManager            val notificationChannel =                NotificationChannel(STEP_CHANNEL_CORE, "#CORE", NotificationManager.IMPORTANCE_LOW)            val notificationChannel2 =                NotificationChannel(                    STEP_CHANNEL_TOOLS,                    "#TOOLS",                    NotificationManager.IMPORTANCE_LOW                )            val notificationChannel3 = NotificationChannel(                STEP_CHANNEL_RECOMMEND,                "#RECOMMEND",                NotificationManager.IMPORTANCE_LOW            )            notificationChannel.description = "#DESCRIPTION"            notificationChannel2.description = "#DESCRIPTION"            notificationChannel3.description = "#DESCRIPTION"            if (notificationManager != null) {                notificationManager.createNotificationChannel(notificationChannel)                notificationManager.createNotificationChannel(notificationChannel2)                notificationManager.createNotificationChannel(notificationChannel3)            }        }    }}