package troll.kotlin.retrofitimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport troll.btc.extensions.onClickimport troll.eth.base.viewbinding.BaseActivityimport troll.kotlin.databinding.ActivityRetrofitBindingclass RetrofitActivity : BaseActivity<ActivityRetrofitBinding>() {    private val vm: RetrofitViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityRetrofitBinding =        ActivityRetrofitBinding.inflate(inflater)    override fun flowData() {        vm.liveData.observe {            bd.retrofitTv.text = it        }    }    override fun flowView() {        bd.button.onClick {            vm.getData()        }    }}