package troll.kotlin.retrofitimport androidx.lifecycle.viewModelScopeimport com.troll.net.RetrofitManagerimport kotlinx.coroutines.launchimport troll.btc.extensions.Liveimport troll.eth.base.viewbinding.BaseViewModelclass RetrofitViewModel : BaseViewModel() {    val liveData = Live<String>()    val api by lazy { RetrofitManager.getApiService() }    fun getData() {        viewModelScope.launch {            val any = api.getArticleList(1)            liveData.value = any.data.total        }    }}