package troll.kotlin.test;import java.util.Arrays;/** * author : TangPeng * date : 8/1/22 15:53 * description : */public class VolatileDemo {    private static volatile long age = 0;    private static class LoopVolatile1 implements Runnable {        @Override        public void run() {            long val = 0;            while (val < 1000000L) {                age++;                val++;            }        }    }    private static class LoopVolatile2 implements Runnable {        @Override        public void run() {            long val = 0;            while (val < 1000000L) {                age++;                val++;            }        }    }    public static void main(String[] args) {        Thread thread = new Thread(new LoopVolatile1());        thread.start();        Thread thread1 = new Thread(new LoopVolatile2());        thread1.start();        while (thread.isAlive() || thread1.isAlive()){            System.out.println("年龄 = " + age);        }    }}