package troll.kotlin.viewpagerimport android.os.Bundleimport android.view.Viewimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport androidx.fragment.app.FragmentPagerAdapterclass MyFragmentPagerAdapter(fm: FragmentManager, val frag: MutableList<TestFragment>, val title: MutableList<String>) :    FragmentPagerAdapter(fm) {    override fun getCount(): Int = title.size    override fun getItem(position: Int): Fragment {        return frag[position]    }    override fun getPageTitle(position: Int): CharSequence {        return title[position]    }    override fun isViewFromObject(view: View, `object`: Any): Boolean {        return view == `object`    }}