package troll.kotlin.viewpagerimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.fragment.app.Fragmentimport troll.btc.constants.EMPTYimport troll.kotlin.Rclass TestFragment : Fragment() {    var string: String = EMPTY    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {        val view = inflater.inflate(R.layout.fragment_test, container, true)        arguments?.let {            string = it.getString("tag").toString()            val tv = view.findViewById<TextView>(R.id.frag_tv)            tv.text = string            println(" 懒加载$string")        }        return view    }    override fun onResume() {        super.onResume()        println("懒加载 $string   onResume")    }    override fun onPause() {        super.onPause()        println("懒加载 $string   onPause onPause")    }}