package troll.kotlin.viewpagerimport android.os.Bundleimport androidx.fragment.app.Fragmentimport androidx.viewpager.widget.ViewPagerimport com.google.android.material.tabs.TabLayoutimport troll.eth.base.BaseActivityimport troll.kotlin.Rclass TestViewPagerActivity : BaseActivity() {    private var layoutTab: TabLayout? = null    private var layoutvp: ViewPager? = null    private val tabList = mutableListOf("关注", "推荐", "最新")    private val frag = mutableListOf(        TestFragment().apply {            arguments = Bundle().also {                it.putString("tag", "关注")            }        },        TestFragment().apply {            arguments = Bundle().also {                it.putString("tag", "关注")            }        },        TestFragment().apply {            arguments = Bundle().also {                it.putString("tag", "关注")            }        })    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_view_pager)        layoutTab = findViewById(R.id.layout_tab)        layoutvp = findViewById(R.id.layout_vp)        layoutvp?.adapter = VpFragment(supportFragmentManager, frag as List<Fragment>?, tabList)        layoutTab?.setupWithViewPager(layoutvp)    }}