package troll.kotlin.viewpager;import androidx.annotation.NonNull;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentStatePagerAdapter;import java.util.List;public class VpFragment extends FragmentStatePagerAdapter {    private List<String> tabNames;    private List<Fragment> fragments;    public VpFragment(FragmentManager fm, List<Fragment> fragments, List<String> tabNames) {        super(fm);        this.tabNames = tabNames;        this.fragments = fragments;    }    @Override    public Fragment getItem(int position) {        return fragments.get(position);    }    @Override    public int getCount() {        return fragments.size();    }    /**     *这个函数就是给TabLayout的Tab设定Title     */    @Override    public CharSequence getPageTitle(int position) {        return tabNames.get(position);    }}