package troll.mviimport android.view.LayoutInflaterimport androidx.activity.viewModelsimport troll.eth.base.viewbinding.ActivityEximport troll.mvi.databinding.ActivityMviBinding/** * MVI 模式 */class MviActivity : ActivityEx<ActivityMviBinding>() {    private val vm: MviViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): ActivityMviBinding = ActivityMviBinding.inflate(inflater)    override fun flowData() {        vm.liveTitle.observe(this) {            renderViewEvent(it)        }    }    private fun renderViewEvent(it: MviViewEvent) {        when (it) {            is MviViewEvent.ShowTitle -> {                binding.mviTv.text = it.title            }        }    }    override fun flowView() {        binding.mviButton.setOnClickListener {            vm.dispatch(MviViewAction.ButtonClick)        }    }}