package troll.mviimport androidx.lifecycle.MutableLiveDataimport troll.btc.extensions.Liveimport troll.eth.base.viewbinding.BaseViewModelclass MviViewModel : BaseViewModel() {     val liveTitle = Live<MviViewEvent>()    fun dispatch(action: MviViewAction) {        when (action) {            is MviViewAction.ButtonClick -> {                buttonClick()            }        }    }    private fun buttonClick() {        liveTitle.value = MviViewEvent.ShowTitle("Miv 模式")    }}