package wan.commonimport android.app.ProgressDialogimport androidx.viewbinding.ViewBindingimport troll.eth.base.viewbinding.BaseActivity/** * author : TangPeng * date : 2/6/23 15:25 * description : */abstract class CommonActivity<BD : ViewBinding> : BaseActivity<BD>(), UiView {    private var progressDialog: ProgressDialog? = null    override fun showLoading() {        if (progressDialog == null) progressDialog = ProgressDialog(this)        progressDialog?.show()    }    override fun dismissLoading() {        progressDialog?.takeIf { it.isShowing }?.dismiss()    }}