package wan.commonimport android.app.ProgressDialogimport androidx.viewbinding.ViewBindingimport troll.eth.base.viewbinding.BaseFragment/** * author : TangPeng * date : 2/1/23 15:37 * description : */abstract class CommonFragment<BD : ViewBinding> : BaseFragment<BD>(), UiView {    private var progressDialog: ProgressDialog? = null    override fun showLoading() {        if (progressDialog == null) progressDialog = ProgressDialog(requireContext())        progressDialog?.show()    }    override fun dismissLoading() {        progressDialog?.takeIf { it.isShowing }?.dismiss()    }}