package wan.common.networkimport wan.common.BuildConfig/** * author : TangPeng * date : 1/31/23 14:13 * description : */class BaseRepo() {    private fun <T> processHttpError(t: Throwable): ErrorResponse<T> {        if (BuildConfig.DEBUG) t.printStackTrace()        return ErrorResponse(t)    }    private fun <T> processHttpOk(resp: BaseResp<T>): BaseResp<T> {        return if (resp.isSuccess) {            val data = resp.data            return if (data == null || data is List<*> && (data as List<*>).isEmpty()) {                EmptyResponse()            } else {                SuccessResponse(data)            }        } else {            FailedResponse(resp.errorCode, resp.errorMsg)        }    }}