package wan.common.network/** * author : TangPeng * date : 2/28/22 17:34 * description : */open class BaseResp<T>(    open val data: T? = null,    open val errorCode: Int? = null,    open val errorMsg: String? = null,    open val error: Throwable? = null,) {    val isSuccess: Boolean        get() = errorCode == 0    override fun toString(): String {        return "BaseResp (data=$data, errorCode=$errorCode, errorMsg=$errorMsg, error=$error)"    }}class EmptyResponse<T> : BaseResp<T>(errorMsg = "空数据")data class SuccessResponse<T>(val response: T) : BaseResp<T>(data = response)data class FailedResponse<T>(override val errorCode: Int?, override val errorMsg: String?) :    BaseResp<T>(errorCode = errorCode, errorMsg = errorMsg)data class ErrorResponse<T>(val throwable: Throwable) : BaseResp<T>(error = throwable)