package wan.common.networkimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport java.util.concurrent.TimeUnitinline fun <reified T : Any> createApi(): T = NetInst.create(T::class.java)object URL {    const val HOST = "https://www.wanandroid.com"}private const val CONNECTION_TIME_OUT = 15Lprivate const val READ_TIME_OUT = 15Lval NetInst: Retrofit by lazy {    Retrofit.Builder()        .baseUrl(URL.HOST)        .addConverterFactory(GsonConverterFactory.create())        .client(            OkHttpClient.Builder()                .connectTimeout(CONNECTION_TIME_OUT, TimeUnit.SECONDS)                .readTimeout(READ_TIME_OUT, TimeUnit.SECONDS).build()        )        .build()}