package wan.homeimport retrofit2.http.GETimport retrofit2.http.Pathimport wan.common.network.BasePagingRespimport wan.common.network.BaseRespimport wan.home.bean.ArticleDataimport wan.home.bean.BannerDataimport wan.home.bean.DailyQuestionData/** * author : TangPeng * date : 2/28/22 17:33 * description : */interface HomeApi {    @GET("wenda/list/{page}/json")    suspend fun getDailyQuestion(@Path("page") page: Int): BaseResp<BasePagingResp<List<DailyQuestionData>>>    @GET("article/list/{page}/json")    suspend fun getHomeArticle(@Path("page") page: Int): BaseResp<BasePagingResp<List<ArticleData>>>    @GET("banner/json")    suspend fun getBanner(): BaseResp<List<BannerData>>}