package wan.home.adapterimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapter/** * author : TangPeng * date : 3/4/22 10:41 * description : */class HomePageAdapter(private val frags: MutableList<Fragment>, fragment: Fragment) :    FragmentStateAdapter(fragment) {    override fun getItemCount(): Int = frags.size    override fun createFragment(position: Int): Fragment = frags[position]}