package wan.home.uiimport android.view.LayoutInflaterimport androidx.fragment.app.viewModelsimport androidx.recyclerview.widget.ConcatAdapterimport androidx.recyclerview.widget.LinearLayoutManagerimport troll.eth.base.viewbinding.BaseFragmentimport wan.home.adapter.ArticleAdapterimport wan.home.adapter.HomeBannerAdapterimport wan.home.databinding.FragmentArticleBindingimport wan.home.viewmodel.ArticleViewModel/** * author : TangPeng * date : 2/28/22 17:49 * description : */class ArticleFragment : BaseFragment<FragmentArticleBinding>() {    private val vm: ArticleViewModel by viewModels()    private val adapter by lazy {        ArticleAdapter {            println("点击点击")        }    }    private val bannerAdapter by lazy {        HomeBannerAdapter {            println("点击点击HomeBannerAdapter")        }    }    override fun getBinding(inflater: LayoutInflater): FragmentArticleBinding =        FragmentArticleBinding.inflate(inflater)    override fun flowData() {        with(vm) {            getHome()            vm.liveArticle.observe(this@ArticleFragment) {                adapter.submitList(it)            }            vm.liveBanner.observe(this@ArticleFragment) {                bannerAdapter.submitList(it)            }        }    }    override fun flowView() {        bd.homeRv.layoutManager = LinearLayoutManager(requireContext())        bd.homeRv.adapter = ConcatAdapter(bannerAdapter, adapter)    }}