package wan.home.uiimport android.view.LayoutInflaterimport androidx.fragment.app.viewModelsimport com.google.android.material.tabs.TabLayoutMediatorimport troll.eth.base.viewbinding.BaseFragmentimport wan.home.adapter.HomePageAdapterimport wan.home.databinding.FragmentHomeBindingimport wan.home.viewmodel.HomeViewModel/** * author : TangPeng * date : 3/4/22 10:28 * description : */class HomeFragment : BaseFragment<FragmentHomeBinding>() {    private val vm: HomeViewModel by viewModels()    override fun getBinding(inflater: LayoutInflater): FragmentHomeBinding =        FragmentHomeBinding.inflate(inflater)    override fun flowData() {        vm.getFragment()        vm.liveFrag.observe(this) {            bd.homeVp.adapter = HomePageAdapter(it, this)            bd.run {                TabLayoutMediator(homeTabLayout, homeVp) { tab, position ->                    when (position) {                        0 -> tab.text = "每日一问"                        1 -> tab.text = "首页"                        2 -> tab.text = "广场"                    }                }.attach()            }        }    }    override fun flowView() {    }}