package wan.home.uiimport android.view.LayoutInflaterimport androidx.fragment.app.viewModelsimport androidx.recyclerview.widget.ConcatAdapterimport androidx.recyclerview.widget.LinearLayoutManagerimport troll.eth.base.viewbinding.BaseFragmentimport wan.home.adapter.HomeArticleAdapterimport wan.home.adapter.HomeBannerAdapterimport wan.home.databinding.FragmentHomeBindingimport wan.home.viewmodel.HomeViewModel/** * author : TangPeng * date : 2/28/22 17:49 * description : */class HomeFragment : BaseFragment<FragmentHomeBinding>() {    private val vm: HomeViewModel by viewModels()    private val adapter by lazy {        HomeArticleAdapter {            println("点击点击")        }    }    private val bannerAdapter by lazy {        HomeBannerAdapter {            println("点击点击HomeBannerAdapter")        }    }    override fun getBinding(inflater: LayoutInflater): FragmentHomeBinding =        FragmentHomeBinding.inflate(inflater)    override fun flowData() {        with(vm) {            getHome()            vm.liveArticle.observe(this@HomeFragment) {                adapter.submitList(it)            }            vm.liveBanner.observe(this@HomeFragment) {                bannerAdapter.submitList(it)            }        }    }    override fun flowView() {        bd.homeRv.layoutManager = LinearLayoutManager(requireContext())        bd.homeRv.adapter = ConcatAdapter(bannerAdapter, adapter)    }}