package wan.home.viewmodelimport androidx.lifecycle.viewModelScopeimport kotlinx.coroutines.launchimport troll.eth.base.viewbinding.BaseViewModelimport wan.home.repo.HomeRepo/** * author : TangPeng * date : 2/28/22 17:31 * description : */class DailyQuestionViewModel : BaseViewModel() {    private val repo by lazy { HomeRepo() }    fun getDailyQuestion(page: Int = 1) {        viewModelScope.launch {            repo.getDailyQuestion(page)        }    }}