package wan.home.viewmodelimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.flow.StateFlowimport kotlinx.coroutines.flow.asStateFlowimport troll.eth.base.viewbinding.BaseViewModelimport wan.home.bean.DailyQuestionDataimport wan.home.repo.HomeRepo/** * author : TangPeng * date : 2/28/22 17:31 * description : */class DailyQuestionViewModel : BaseViewModel() {    private val repo by lazy { HomeRepo() }    private val _questionState =        MutableStateFlow(mutableListOf<DailyQuestionData>().toMutableList())    val questionState: StateFlow<MutableList<DailyQuestionData>> = _questionState.asStateFlow()    suspend fun getDailyQuestion(page: Int = 1) {        _questionState.value = repo.getDailyQuestion(page).data?.datas?.toMutableList()            ?: emptyList<DailyQuestionData>().toMutableList()    }}