package wan.home.viewmodelimport androidx.lifecycle.viewModelScopeimport kotlinx.coroutines.launchimport troll.btc.extensions.Liveimport troll.eth.base.viewbinding.BaseViewModelimport wan.home.bean.DailyQuestionDataimport wan.home.repo.HomeRepo/** * author : TangPeng * date : 2/28/22 17:31 * description : */class DailyQuestionViewModel : BaseViewModel() {    private val repo by lazy { HomeRepo() }    val liveQuestion = Live<MutableList<DailyQuestionData>>()    fun getDailyQuestion(page: Int = 1) {        viewModelScope.launch {            liveQuestion.value = repo.getDailyQuestion(page).data?.datas?.toMutableList()        }    }}