package wan.home.viewmodelimport androidx.fragment.app.Fragmentimport troll.btc.extensions.Liveimport troll.eth.base.viewbinding.BaseViewModelimport wan.home.ui.ArticleFragmentimport wan.home.ui.DailyQuestionFragmentimport wan.home.ui.SquareFragment/** * author : TangPeng * date : 3/4/22 10:46 * description : */class HomeViewModel : BaseViewModel() {    val liveFrag = Live<MutableList<Fragment>>()    fun getFragment() {        liveFrag.value = mutableListOf<Fragment>().apply {            add(DailyQuestionFragment())            add(ArticleFragment())            add(SquareFragment())        }    }}