package wan.home.viewmodelimport androidx.lifecycle.viewModelScopeimport kotlinx.coroutines.launchimport troll.btc.extensions.Liveimport troll.eth.base.viewbinding.BaseViewModelimport wan.home.bean.SquareDataimport wan.home.repo.HomeRepo/** * author : TangPeng * date : 2/28/22 17:55 * description : */class SquareViewModel : BaseViewModel() {    private val repo = HomeRepo()    val liveSquare = Live<MutableList<SquareData>>()     fun getSquare(page: Int = 1) {        viewModelScope.launch {            liveSquare.value = repo.getSquare(page).data?.datas?.toMutableList()        }    }}