package wan.home.viewmodelimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.flow.StateFlowimport kotlinx.coroutines.flow.asStateFlowimport troll.eth.base.viewbinding.BaseViewModelimport wan.home.bean.SquareDataimport wan.home.repo.HomeRepo/** * author : TangPeng * date : 2/28/22 17:55 * description : */class SquareViewModel : BaseViewModel() {    private val repo = HomeRepo()    private val _uiState = MutableStateFlow(mutableListOf<SquareData>())    val uiState: StateFlow<MutableList<SquareData>> = _uiState.asStateFlow()    suspend fun getSquare(page: Int = 1) {        _uiState.value = repo.getSquare(page).data?.datas?.toMutableList()            ?: emptyList<SquareData>().toMutableList()    }}