package com.troll.loginimport troll.btc.constants.EMPTY/** * author : TangPeng * date : 3/29/22 09:40 * description : */data class LoginViewState(val name: String = EMPTY, val password: String = EMPTY) {    val loginEnable: Boolean        get() = name.isNotEmpty() && password.length >= 6    val passwordTip: Boolean        get() = password.length < 6}sealed class LoginViewEvent {    data class ShowToast(val message: String) : LoginViewEvent()    object ShowLoading : LoginViewEvent()    object DismissLoading : LoginViewEvent()    object LoginSuccess : LoginViewEvent()}sealed class LoginViewAction {    data class RefreshPasswordTips(val password: String) : LoginViewAction()    data class Login(val account: String, val password: String) : LoginViewAction()}