package wan.mainimport android.view.LayoutInflaterimport androidx.navigation.fragment.NavHostFragmentimport androidx.navigation.fragment.findNavControllerimport androidx.navigation.ui.NavigationUIimport troll.eth.base.viewbinding.BaseActivityimport wan.main.databinding.ActivityWanBinding/** * author : TangPeng * date : 2/28/22 18:56 * description : 主页面 */class WanActivity : BaseActivity<ActivityWanBinding>() {    override fun getBinding(inflater: LayoutInflater): ActivityWanBinding =        ActivityWanBinding.inflate(inflater)    override fun flowData() {    }    override fun flowView() {        val navHostFragment =            supportFragmentManager.findFragmentById(R.id.main_layout_container) as NavHostFragment        val controller = navHostFragment.findNavController()        NavigationUI.setupWithNavController(bd.mainLayoutNav, controller)    }}