package wan.projectimport retrofit2.http.GETimport retrofit2.http.Pathimport retrofit2.http.Queryimport wan.common.network.BasePagingRespimport wan.common.network.BaseRespimport wan.project.bean.ProjectContentimport wan.project.bean.ProjectTree/** * author : TangPeng * date : 3/4/22 15:21 * description : */interface ProjectApi {    @GET("project/tree/json")    suspend fun loadProjectTree(): BaseResp<List<ProjectTree>>    @GET("project/list/{path}/json")    suspend fun loadContentById(        @Path("path") path: Int,        @Query("cid") cid: Int    ): BaseResp<BasePagingResp<List<ProjectContent>>>}