package wan.projectimport android.view.LayoutInflaterimport androidx.fragment.app.viewModelsimport androidx.recyclerview.widget.LinearLayoutManagerimport troll.eth.base.viewbinding.BaseFragmentimport wan.project.databinding.FragmentProjectBinding/** * author : TangPeng * date : 3/2/22 15:28 * description : 项目 */class ProjectFragment : BaseFragment<FragmentProjectBinding>() {    private val vm: ProjectViewModel by viewModels()    private val adapter = ProjectAdapter {        println("项目 ${it.name}")    }    override fun getBinding(inflater: LayoutInflater): FragmentProjectBinding =        FragmentProjectBinding.inflate(inflater)    override fun flowData() {        vm.getProjectTree()        vm.liveProjectTree.observe(this) {            adapter.submitList(it)        }    }    override fun flowView() {        bd.projectRv.layoutManager = LinearLayoutManager(requireContext())        bd.projectRv.adapter = adapter    }}