package wan.projectimport androidx.lifecycle.viewModelScopeimport kotlinx.coroutines.launchimport troll.btc.extensions.Liveimport troll.eth.base.viewbinding.BaseViewModelimport wan.common.network.createApiimport wan.project.bean.ProjectContent/** * author : TangPeng * date : 3/7/22 15:45 * description : */class ProjectTreeViewModel : BaseViewModel() {    private val api = createApi<ProjectApi>()    val liveData = Live<MutableList<ProjectContent>>()    fun getData(cid: Int) {        viewModelScope.launch {            liveData.value = api.loadContentById(page = 1, cid = cid).data?.datas?.toMutableList()        }    }}