package wan.projectimport androidx.lifecycle.viewModelScopeimport kotlinx.coroutines.launchimport troll.btc.extensions.Liveimport troll.eth.base.viewbinding.BaseViewModelimport wan.common.network.createApiimport wan.project.bean.ProjectTree/** * author : TangPeng * date : 3/4/22 15:20 * description : */class ProjectViewModel : BaseViewModel() {    private val api = createApi<ProjectApi>()    val liveProjectTree = Live<MutableList<ProjectTree>>()    fun getProjectTree() {        viewModelScope.launch {            liveProjectTree.value = api.loadProjectTree().data?.toMutableList()        }    }}